mapper.select:
select id, c_ip, c_icon, c_date, d_id, a_id, c_type, c_toid, c_content
from comment
where id = #{id,jdbcType=INTEGER}

select * from comment where c_type = 2
and a_id = #{a_id}
order by c_date desc
LIMIT #{curr},#{limit}

select * from comment where c_type = 1
   and d_id = #{d_id}
   order by c_date desc
   LIMIT #{curr},#{limit}
